import React, { Component } from 'react';
import F1InfoAPI from "../../API/InfoAPI";
import Select from 'react-select';

interface SelectSceletonState {
    selectedOption: any;
    driverOptions: any[];
}

class SelectSceleton extends Component<any, SelectSceletonState> {
    state: SelectSceletonState = {
        selectedOption: null,
        driverOptions: [],
    };

    async componentDidMount() {
        const options = new F1InfoAPI();
        try {
            const driverData = await options.Drivers("f1/info/drivers");
            const driverOptions = driverData.map(driver => ({
                value: driver.id,
                label: driver.name,
            }));
            this.setState({ driverOptions });
        } catch (error) {
            // Handle errors here
        }
    }

    handleChange = (selectedOption: any) => {
        this.setState({ selectedOption }, () =>
            console.log(`Option selected:`, this.state.selectedOption)
        );
    };

    render() {
        const { selectedOption, driverOptions } = this.state;

        return (
            <Select
                value={selectedOption}
                onChange={this.handleChange}
                options={driverOptions}
            />
        );
    }
}

export default SelectSceleton;
