import React, { Component } from 'react';
import "./index.css";
import ConnectionVerifyAPI from "./API/ConnectionVerifyAPI";
import F1InfoAPI from "./API/InfoAPI";

class App extends Component<any> {
    constructor(props: any) {
        super(props);
        this.state = {
            isConnected: false,
        };
    }

    componentDidMount() {
        const response = new F1InfoAPI();
        response.Drivers().then((value) => {
            this.setState({ isConnected: value });
        });
    }

    render() {
        const isConnected  = this.state;

        if (isConnected) {
            return (
                <div className={"flex flex-col justify-center items-center h-[100vh] w-full bg-amber-50"}>
                    <div>Hello World</div>
                    <div></div>
                </div>
            );
        } else {
            return (
                <div className="w-[100wv] h-[100vh] flex flex-col justify-center items-center bg-black">
                    <p></p>
                </div>
            );
        }
    }
}

export default App;
