export default class  F1InfoAPI{

     Drivers(url: string) {
        const result = fetch(url)
            .then(response => {
                if (!response.ok) {
                    throw new Error(response.statusText)
                }
                return response.json().then((value) => {
                    return value.MRData.DriverTable.Drivers;
                })
            })
        var driversarray: any[] =[]
         var emptylist: any[] = []
        result.then((resultvalue) =>{return driversarray = resultvalue;})
        setTimeout(()=>{return driversarray.map((elem)=> {
            emptylist.push({value:elem.driverId, label:elem.givenName + " " + elem.familyName})
            return emptylist
        })}, 400)
    }
};
