import React, { Component } from 'react';
import "./index.css";
import F1InfoAPI from "./API/InfoAPI";
import SelectSceleton from "./components/select/SelectSceleton";

class App extends Component<any, { infoOptions: any, selectedOption: any}> {
    constructor(props: any) {
        super(props);
        this.state = {
            infoOptions: [],
            selectedOption: null
        };
    }

    async componentDidMount() {
        const options: F1InfoAPI = new F1InfoAPI();
        try {
            const infoOptions = await options.Drivers("http://localhost:8080/f1/info/drivers");
            this.setState({ infoOptions });
        } catch (error) {
            // Handle errors here
        }
    }

    handleSelectChange = (selectedOption: any) => {
        // Handle the selected option here
        console.log("Selected option:", selectedOption);
    }

    render() {
        return (
            <div className={"flex flex-col justify-center items-center h-[100vh] w-full bg-amber-50"}>
                <div>Hello World</div>
                <SelectSceleton
                    onChange={this.handleSelectChange}
                    options={this.state.infoOptions}
                    value={this.state.selectedOption} // You can set the initial value here
                />
                <div></div>
            </div>
        );
    }
}

export default App;
