import React, { Component } from 'react';
import F1InfoAPI from "../../API/InfoAPI";
import Select from 'react-select';

class SelectSceleton extends Component<any> {
    state = {
        selectedOption: null,
        driverOptions: [], // Store the driver options here
    };

    async componentDidMount() {
        const options: F1InfoAPI = new F1InfoAPI();
        try {
            const driverOptions: any[] = await options.Drivers("f1/info/drivers");
            this.setState({ driverOptions });
        } catch (error) {
        }
    }

    render() {
        const { selectedOption , driverOptions } = this.state;
        return (
            <Select
                value={selectedOption}
                // onChange={this.handleChange}
                options={driverOptions}
            />
        );
    }
}

export default SelectSceleton;
